public class HighestValuePalindrome {

	// Complete the highestValuePalindrome function below.
	static String highestValuePalindrome(String s, int n, int k) {
		int palindromeRange = palindromeRange(s, k);
		if (palindromeRange == -1) {
			return "-1";
		}

		if (n == 1 && k == 1)
			return "9";

		if (k == 0 && isPalindrome(s, 0))
			return s;

		if (k == 1 && s.length() % 2 > 0)
			if (isPalindrome(s, 0))
				return getStringWith9(s, (s.length() / 2), true);
			else
				return "-1";

		if (k > n) {
			String all9s = "";
			for (int i = 0; i < n; i++)
				all9s = all9s.concat("9");
			return all9s;
		}

		String maxP = s;
		int changes = 0;
		for (int i = 0; i < n && changes <= k; i++) {
			if (s.charAt(i) != s.charAt(n - 1 - i)) {
				maxP = getStringWith9(maxP, i, (k - changes) > 1);
				changes++;
			}
		}
		if ((k - changes) >= 2) {
			for (int i = 0; i < n && changes < k; i++) {
				if (s.charAt(i) != '9') {
					maxP = getStringWith9(maxP, i, true);
					changes += 2;
				}
			}
		}
		if (isPalindrome(maxP, 0))
			return "" + maxP;
		return "-1";
	}

	static String getStringWith9(String s, int i, boolean shouldMake9) {
		if (shouldMake9) {
			return replaceCharAt(replaceCharAt(s, i, "9"), s.length() - i - 1, "9");
		}
		return s.substring(0, i) +
				maxChar(s.charAt(i), s.charAt(s.length() - 1 - i)) +
				s.substring(i + 1, s.length());
	}

	static String replaceCharAt(String s, int i, String replace) {
		StringBuilder b = new StringBuilder(s);
		b.setCharAt(i, replace.charAt(0));
		return b.toString();
	}

	static boolean isPalindrome(String s, int i) {
		if ((i - s.length() / 2) <= 1)
			return true;
		if (s.charAt(i) == s.charAt(s.length() - i))
			return isPalindrome(s, i++);
		return false;
	}

	static String maxChar(char a, char b) {
		if (Integer.valueOf(a) > Integer.valueOf(b))
			return "" + a;
		return "" + b;
	}

	static int palindromeRange(String s, int k) {
		String a = s.substring(0, s.length() / 2);
		String b = new StringBuilder(s.substring(s.length() / 2, s.length())).reverse().toString();
		if (a.equals(b))
			return 0;
		else {
			int count = 0;
			for (int i = 0; i < s.length() / 2; i++) {
				if (a.charAt(i) != b.charAt(i))
					count++;
			}
			if (count <= k)
				return count;
			return -1;
		}
	}

	public static void main(String[] args) throws IOException {
		Test.equals(highestValuePalindrome("0011", 4, 1), "-1");
		Test.equals(highestValuePalindrome("3943", 4, 1), "3993");
		Test.equals(highestValuePalindrome("092282", 6, 3), "992299");
		Test.equals(highestValuePalindrome("777", 3, 0), "777");
		Test.equals(highestValuePalindrome("5", 1, 1), "9");
		Test.equals(highestValuePalindrome("932239", 6, 2), "992299");
		Test.equals(highestValuePalindrome("12321", 5, 1), "12921");

		Test.equals(highestValuePalindrome(
				"",
				37455, 79655),
				"");

		Test.equals(highestValuePalindrome(
				"",
				24481, 50356),
				"");
	}

}
